function setProps(){var o=(new Date).getFullYear();$("#year").text(o),$(".hidden-info").hide(),$(".show-info").click(function(){$(".hidden-info").toggle()});var n=$(window).width();n-=30,$("#openseadragon").css({width:n});var t=$(window).height();t-=80,$("#openseadragon").css({height:t}),$(window).scroll(function(){$(this).scrollTop()>200?$(".go-top").fadeIn(200):$(".go-top").fadeOut(200)}),$(".go-top").click(function(o){o.preventDefault(),$("html, body").animate({scrollTop:0},300)})}function goBack(){window.history.back()}$("document").ready(function(){setProps()});